// ----------------------------------------------------------------------------
// Owned contract
// ----------------------------------------------------------------------------
contract OwnedChallenge {
    address public owner;
    address public newOwner;

    event OwnershipTransferred(address indexed _from, address indexed _to);

    constructor() public {
        owner = msg.sender;
    }

    modifier onlyOwner {
        require(msg.sender == owner);
        _;
    }

    function transferOwnership(address _newOwner) public onlyOwner {
        newOwner = _newOwner;
    }
    function acceptOwnership() public {
        require(msg.sender == newOwner);
        emit OwnershipTransferred(owner, newOwner);
        owner = newOwner;
        newOwner = address(0);
    }
}



contract Torneo is OwnedChallenge {
	string name;
	string description;
	uint prize;
	uint fee;
	uint target;
	string targetDescription;
	address contract_address;

	//Building Dictionary of partecipants
  	mapping(address => uint) partecipants;

	//Constructor
	constructor(
		string initialName,
		string initialDescription,
		uint initialPrize,
		uint initialFee,
		uint initialTarget,
		string initialTargetDescription
	) public {
		name = initialName;
		description = initialDescription;
		prize = initialPrize;
		fee = initialFee;
		target = initialTarget;
        targetDescription = initialDescription;
		contract_address = this; //this cointains contract's address information
	}

    function partecipate(uint tokens) public returns(bool success) {
        require(tokens == 1);

        //if (msg.sender == )
        return true;
    }




}
